import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Surveys from './components/Surveys';
import AdminPanel from './components/AdminPanel';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route exact path="/" render={() => (
            isLoggedIn ? <Redirect to="/surveys" /> : <Redirect to="/login" />
          )} />
          <Route path="/login" render={(props) => (
            <Login {...props} setIsLoggedIn={setIsLoggedIn} setIsAdmin={setIsAdmin} />
          )} />
          <Route path="/register" component={Register} />
          <Route path="/surveys" render={(props) => (
            isLoggedIn ? <Surveys {...props} /> : <Redirect to="/login" />
          )} />
          <Route path="/admin" render={(props) => (
            isLoggedIn && isAdmin ? <AdminPanel {...props} /> : <Redirect to="/login" />
          )} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
